import React from "react";
import { connect } from "react-redux";



class ConnectedList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            articles: [],
            tombol: false
        }

        this.ontombol = this.ontombol.bind(this);

    }

    ontombol() {
        this.setState(prevState => ({
            tombol: !prevState.tombol
        }));
    }

    render() {
        return (
            <div>

                {this.state.tombol ? this.state.articles.map(el => (
                        <li className="list-group-item" key={el.id}>
                            {el.title}
                        </li>
                    ))
                 : 
                    this.state.articles.map(el => (
                        <input className="list-group-item" key={el.id} value={el.title} />
                        
                    ))
                })}

            </div>

        )
    }


}



const List = connect()(ConnectedList);
export default List;
