import React from "react";
import { connect } from "react-redux";

const mapStateToProps = state => {
    return { 
        articles: state.articles
    };
};


class ConnectedList extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            berubah : false,
            edit : null
        }
        this.onberubah = this.onberubah.bind(this);
    }

    onberubah(id){
        alert(id);
        // this.setState({
        //     edit : articles,
        //     berubah : !this.state.berubah
        // });
    }

    render(){
        
        return (
    <div className="list-group list-group-flush">
        {this.props.articles.map( el => (
                <div onClick={()=>this.onberubah(el.id)} className="list-group-item" key={el.id}>
                    {this.state.berubah ? <input value={el.title} autoFocus></input> : <li>{el.title}</li>}
                </div> 
        ))}
    </div>
    );

    }
};

const List = connect(mapStateToProps)(ConnectedList);
export default List;
