import React from "react";
import { connect } from "react-redux";
import { RemoveTodo , UpdateTodo } from "../action/index";

const mapStateToProps = state => {
    return { 
        articles: state.articles
    };
};
function mapDistpatchToProps(distpatch) {
    return{
        RemoveTodo: articles => distpatch(RemoveTodo(articles))
    }
}

function mapUpdateToProps(distpatch) {
    return{
        UpdateTodo: articles(UpdateTodo)
    }
}
class ConnectedList extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            berubah : false,
            id: 1,
            edit: []
        }
        this.handleChange = this.handleChange.bind(this);
    }
    handleChange(id, value){
        var d = Object.assign({}, this.state.edit)
        Object.assign(d, { [id]: value })
        this.setState({
            berubah: !this.state.berubah,
            id: value
        });
        setTimeout(() => {
            console.log(this.state)
        }, 1500)
    }
    render(){   
        return (
    <div className="list-group list-group-flush">
        {this.props.articles.map(( el, key) => (
                <div>
                {this.state.id === el.id && this.state.berubah ? 
               <input onDoubleClick={() => this.handleChange()} autoFocus placeholder={el.title}></input> 
                : 
                <li onDoubleClick={(d) => this.handleChange('id', d.target.value)} value={el.id}>{el.title}
                        <button onClick={e => this.props.RemoveTodo({value : e.target.value})} value={key}>
                            hapus
                        </button>
                </li>}
                </div> 
        ))}
    </div>
    );
    }
};

const List = connect(mapStateToProps,mapDistpatchToProps)(ConnectedList);
export default List;
