import React from "react";
import { connect } from "react-redux";

const mapStateToProps = state => {
    return { 
        articles: state.articles
    };
};


class ConnectedList extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            berubah : false,
            edit : null
        }
        this.onberubah = this.onberubah.bind(this);
    }

    onberubah(id,value){
        alert(value);
        const { items } = this.props;
        this.setState({
            items: items.filter(item => item.phone === id)
        })
    }

    render(){
        
        return (
    <div className="list-group list-group-flush">
        {this.props.articles.map( el => (
                <div key={el.id}>
                {this.state.berubah ? <input value={el.title} onClick={(e) => this.onberubah('id', e.target.value)} onClick={(e) => this.onberubah(e.target.key, e.target.key)} key={el.id} autoFocus></input> : <li onClick={(e) => this.onberubah('id', e.target.value)} value={el.id} key={el.id}>{el.title}</li>}
                </div> 
        ))}
    </div>
    );

    }
};

const List = connect(mapStateToProps)(ConnectedList);
export default List;
