import React from "react";
import { connect } from "react-redux";

const mapStateToProps = state => {
    return { 
        articles: state.articles
    };
};


class ConnectedList extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            berubah : false,
            edit : null,
            id: 1,
        }
        this.onberubah = this.onberubah.bind(this);
    }

    onberubah(id,value){
        this.setState({
            berubah : !this.state.berubah,
            id: value
        });
    }

    render(){
        
        return (
    <div className="list-group list-group-flush">
        {this.props.articles.map( el => (
                <div key={el.id}>
                {this.state.id === el.id && this.state.berubah ? <input value={el.title} onClick={() => this.onberubah('id', el.id)} autoFocus></input> : <li onClick={(e) => this.onberubah('id', el.id)}>{el.title}</li>}
                </div> 
        ))}
    </div>
    );

    }
};

const List = connect(mapStateToProps)(ConnectedList);
export default List;
