import React from "react";
import { connect } from "react-redux";



class ConnectedList extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            articles : [],
            tombol : false
        }

    }

    ontombol () {
        this.setState(prevState => ({
            tombol: !prevState.tombol
        }));
    }

    render(){
        return(
            <div>
                <button onClick={ontombol}>
                {this.state.tombol ? 'ada' : 'tidak'}
                </button>
            </div>

        )
    }


}



const List = connect()(ConnectedList);
export default List;
