import React from "react";
import { connect } from "react-redux";
import { RemoveTodo } from "../action/index";

const mapStateToProps = state => {
    return { 
        articles: state.articles
    };
};

function mapDistpatchToProps(distpatch) {
    return{
        RemoveTodo: articles => distpatch(RemoveTodo(articles))
    }
}


class ConnectedList extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            berubah : false,
            id: 1,
            edit: null
        }
        this.handleChange = this.handleChange.bind(this);
    }

   
    handleChange(id, value){
        var d = Object.assign({}, this.state.edit)
        Object.assign(d, { [id]: value })
        this.setState({
            berubah: !this.state.berubah,
            id: value,
            edit: d
        });
    }

    hapus(e, value){
        const id = this.state.id;
        this.props.RemoveTodo( {id} );
        this.setState({
            id: this.state.id + 1
        });
        alert(value);
    }


    render(){
        
        return (
    <div className="list-group list-group-flush">
        {this.props.articles.map( el => (
                <div key={el.id}>

                {this.state.id === el.id && this.state.berubah ? 
               
               <input value={el.title} onDoubleClick={() => this.handleChange('id', el.id)} autoFocus></input> 
                : 
                <li onDoubleClick={() => this.handleChange('id', el.id)}>{el.title}
                        <button onClick={() => this.hapus(e.target.value)} value={el.id}>
                            hapus
                        </button>

                
                </li>}
               
                </div> 
                
        ))}
    </div>
    );

    }
};

const List = connect(mapStateToProps,mapDistpatchToProps)(ConnectedList);
export default List;
