import React from "react";
import { connect } from "react-redux";
import { RemoveTodo } from "../action/index";

const mapStateToProps = state => {
    return { 
        articles: state.articles
    };
};

function mapDistpatchToProps(distpatch) {
    return{
        RemoveTodo: articles => distpatch(RemoveTodo(articles))
    }
}

class ConnectedList extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            berubah : false,
            id: 1,
            edit: null
        }
        this.handleChange = this.handleChange.bind(this);
    }

   
    handleChange(id, value){
        this.setState({
            berubah: !this.state.berubah,
            edit: value
        });
        alert(value);
    }


    render(){
        
        return (
    <div className="list-group list-group-flush">
        {this.props.articles.map(( el, key) => (
                <div>

                {this.state.id === el.id && this.state.berubah ? 
               
               <input value={el.title} onDoubleClick={d => this.handleChange('id', el.id)} autoFocus></input> 
                : 
                <li onDoubleClick={(d) => this.handleChange('id', d.target.value)} value={el.id}>{el.title}
                        <button onClick={e => this.props.RemoveTodo({value : e.target.value})} value={key}>
                            hapus
                        </button>

                
                </li>}
               
                </div> 
                
        ))}
    </div>
    );

    }
};

const List = connect(mapStateToProps,mapDistpatchToProps)(ConnectedList);
export default List;
