import React from "react";
import { connect } from "react-redux";

const mapStateToProps = state => {
    return { 
        articles: state.articles
    };
};


class ConnectedList extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            berubah : false,
            edit : null
        }
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(id,value){

        var e = Object.assign({}, this.state.editdata)
        Object.assign(e, { [id]: value })
        this.setState({
            editdata: e
        });
        setTimeout(() => {
            console.log(this.state)
        }, 1500)
        this.setState({
            berubah : !this.state.berubah
        });
    }

    render(){
        
        return (
    <div className="list-group list-group-flush">
        {this.props.articles.map( el => (
                <div  className="list-group-item" key={el.id}>
                {this.state.berubah ? <input value={el.title} onClick={(e) => this.handleChange("id", e.target.value)} autoFocus></input> : <li value={el.title} onClick={(e) => this.handleChange("id", e.target.value)}>{el.title}</li>}
                </div> 
        ))}
    </div>
    );

    }
};

const List = connect(mapStateToProps)(ConnectedList);
export default List;
